import store from '../components/store.js'
export default {
  name: 'Info',
  methods: {
    logout () {
      const token = store.state.token
      if (token) {
        this.$store.commit('LOG_OUT')
        this.$message('byebye')
        this.$router.replace({ name: 'home' })
      } else {
        this.$message('您还未登录')
      }
    }
  }
}
